calc.equation_logic_text:
var calc_output = [];

var age = parseFloat(age)
var smoking = parseFloat(smoking)
var dm = parseFloat(dm)
var mi = parseFloat(mi)
var priorpci = parseFloat(priorpci)
var stent = parseFloat(stent)
var chf = parseFloat(chf)
var veinstent = parseFloat(veinstent)
var stentdiameter = parseFloat(stentdiameter)

var score = age + smoking + dm + mi + priorpci + stent + chf + veinstent + stentdiameter

var risk, rec

if (score >= 2) {
	risk = 'High'
	rec = 'Prolonged DAPT recommended'
}
else {
	risk = 'Low'
	rec = 'Prolonged DAPT not recommended'
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: rec
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'DAPT Score'
});
calc_output.push({
    name: 'risk',
    value: risk,
    value_text: 'risk',
    message: rec
});
