calc.equation_logic_text:
var calc_output = [];

var age = parseFloat(age)
var cr = parseFloat(cr)
var hr = parseFloat(hr)
var bp = parseFloat(bp)
var troponin = parseFloat(troponin)
var hf = parseFloat(hf)
var stdep = parseFloat(stdep)
var pci = parseFloat(pci)
var lung = parseFloat(lung)

var score = age + cr + hr + bp + troponin + hf + stdep + pci + lung

var risk = [ "3.4", "3.4", "4.9", "5.5", "6.9", "9.3", "12", "14.6", "17.4", "20.8", "23.3", "27.7", "31", "31.5", "39.3", ">39.3", ">39.3", ">39.3", ">39.3", ">39.3", ">39.3" ]

var comp = risk[score]

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: comp + '% risk'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'ACTION ICU Score'
});
calc_output.push({
    name: 'comp',
    value: comp,
    value_text: '%',
    message: 'Risk of complications requiring ICU care among initially uncomplicated patients with NSTEMI (cardiac arrest, shock, high-grade AV block, respiratory failure, stroke, or death during index admission)'
});
