calc.equation_logic_text:
var calc_output = [];

var age = parseFloat(age)
var oxygen = parseFloat(oxygen)
var infection = parseFloat(infection)
var anemia = parseFloat(anemia)
var incision = parseFloat(incision)
var duration = parseFloat(duration)
var emergency = parseFloat(emergency)

var score = age + oxygen + infection + anemia + incision + duration + emergency

var risk, rate

if (score < 26) {
	risk = 'Low'
	rate = 1.6
}
else if (score >= 26 && score <= 44) {
	risk = 'Intermediate'
	rate = 13.3
}
else {
	risk = 'High'
	rate = 42.1
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk + ' risk'
});
calc_output.push({
    name: '10022_ARISCAT Score_result',
    value: score,
    value_text: 'points',
    message: 'ARISCAT Score'
});
calc_output.push({
    name: '10022_ARISCAT Score_interpretation',
    value: risk, 
    value_text: 'risk',
    message: rate + '% risk of in-hospital post-op pulmonary complications (composite including respiratory failure, respiratory infection, pleural effusion, atelectasis, pneumothorax, bronchospasm, aspiration pneumonitis)'
});
