calc.equation_logic_text:
var calc_output = [];

var age = parseFloat(age)
var sex = parseFloat(sex)
var joints = parseFloat(joints)
var symmetric = parseFloat(symmetric)
var stiffness = parseFloat(stiffness)
var tender = parseFloat(tender)
var swollen = parseFloat(swollen)
var crp = parseFloat(crp)
var rf = parseFloat(rf)
var ccp = parseFloat(ccp)

var score = age * 0.02 + sex + joints + symmetric + stiffness + tender + swollen + crp + rf + ccp
var score = score.toFixed(1)

var risk, rate

if (score <= 6.0) {
	risk = 'Low'
	rate = 'NPV 83%'
}
else if (score > 6.0 && score < 8.0) {
	risk = 'Indeterminate'
	rate = ''
}
else {
	risk = 'High'
	rate = 'PPV 97%'
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk + ' risk'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'Leiden Rule for Undifferentiated Arthritis'
});
calc_output.push({
    name: 'Score',
    value: risk, 
    value_text: 'risk',
    message: rate 
});
