var calc_output = [];

var age = parseFloat(age)
var gender = parseFloat(gender)
var indication = parseFloat(indication)
var bmi = parseFloat(bmi)
var ductdiameter = parseFloat(ductdiameter)
var gallbladderwall = parseFloat(gallbladderwall)
var preopct = parseFloat(preopct)
var cholangiogram = parseFloat(cholangiogram)
var surgadmits = parseFloat(surgadmits)
var asa = parseFloat(asa)

var score = age + gender + indication + bmi + ductdiameter + gallbladderwall + preopct + cholangiogram + surgadmits + asa 

var risk 
var optime

if (score <=3.5) {
    risk = 'Low'
    optime = '5.1'   
}    
else if (score >= 4 && score <= 8) { 
    risk = 'Intermediate'
    optime = '5.1-41.8' 
}
else {
    risk = 'High'
    optime = '41.8'
} 

//results 
calc_output.push({
    name: 'mini',
    value: optime,
    value_text: '%',
    message: risk + ' chance of OR time >90 mins'
    });
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'CholeS Score'
    });
