calc.equation_logic_text:
var calc_output = [];
var peripheral = parseFloat(peripheral);
var backpain = parseFloat(backpain);
var stiffness = parseFloat(stiffness);
var global = parseFloat(global);
var esr = parseFloat(esr);

var index = 0.08*backpain + 0.07*stiffness + 0.11*global + 0.09*peripheral + 0.29*Math.sqrt(esr) 
var index2 = index.toFixed(1)

var activity

if (index2 < 1.3) {
  activity = 'Inactive'
}
else if (index2 >= 1.3 && index2 < 2.1) {
  activity = 'Low'
}
else if (index2 >= 2.1 && index2 <= 3.5) {
    activity = 'High'
}

else {
    activity = 'Very high'
}

//results
calc_output.push({
    name: 'mini',
    value: index2,
    value_text: 'points',
    message: activity + ' disease activity'
});
calc_output.push({
    name: 'index',
    value: index2,
    value_text: 'points',
    message: 'ASDAS-ESR'
});
calc_output.push({
    name: 'activity',
    value: activity,
    value_text: '',
    message: 'Disease activity'
});
