var calc_output = [];

var hb = parseFloat(hb)
var onco = parseFloat(onco)
var na = parseFloat(na)
var icd = parseFloat(icd)
var admit = parseFloat(admit)
var admitsyr = parseFloat(admitsyr)
var lengthofstay = parseFloat(lengthofstay)

var score = hb + onco + na + icd + admit + admitsyr + lengthofstay

var risk
var readmission


if (score >= 0 && score <=4) {
    risk = 'Low' 
    readmission = '5.8'
}    
else if (score >= 5 && score <=6) { 
    risk = 'Intermediate'
    readmission = '12.0'
}
else {
    risk = 'High'
    readmission = '22.8'
}

//results 
calc_output.push({
    name: 'mini',
    value: readmission,
    value_text: '%',
    message: 'Readmission risk' 
    });
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'HOSPITAL Score'
    });
calc_output.push({
    name: 'risk',
    value: risk, 
    value_text: '',
    message: 'Risk group'
});
calc_output.push({
    name: 'readmission',
    value: readmission, 
    value_text: '%',
    message: 'Risk of potentially avoidable 30-day readmission (see Evidence for details)'
});
