calc.equation_logic_text:
var calc_output = [];

var wellbeing = parseFloat(wellbeing);
var abpain = parseFloat(abpain);
var stools = parseFloat(stools);
var mass = parseFloat(mass);
var comp1 = parseFloat(comp1);
var comp2 = parseFloat(comp2);
var comp3 = parseFloat(comp3);
var comp4 = parseFloat(comp4);
var comp5 = parseFloat(comp5);
var comp6 = parseFloat(comp6);
var comp7 = parseFloat(comp7);
var comp8 = parseFloat(comp8);

var score = wellbeing + abpain + stools + mass + comp1 + comp2 + comp3 + comp4 + comp5 + comp6 + comp7 + comp8;

var index; 


if (score <5) {
    index = 'Remission';
}    
else if (score >= 5 && score <=7) { 
    index = 'Mild disease';
}
else if (score >= 8 && score <=16) { 
    index = 'Moderate disease';
}
else {
    index = 'Severe disease';
}

//results 
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: index 
    });
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'Harvey-Bradshaw Index'
    });
calc_output.push({
    name: 'Index',
    value: index, 
    value_text: '',
    message: ''
});
