calc.equation_logic_text:
var calc_output = []; 
var resp = parseFloat(resp);
var scale1 = parseFloat(scale1);
var scale2 = parseFloat(scale2);
var room = parseFloat(room);
var temp = parseFloat(temp);
var bp = parseFloat(bp);
var pulse = parseFloat(pulse);
var acvpu = parseFloat(acvpu);
var failure = parseFloat(failure);


if (failure == 0){
	var u = resp + scale1 + room + temp + bp + pulse + acvpu
}

if (failure == 1){
	var u = resp + scale2 + room + temp + bp + pulse + acvpu
}

var risk;
var response;
var freq;

if (u >= 0 && u <= 4){
  if (resp == 3||scale1 == 3||scale2 == 3||temp == 3||bp == 3||pulse == 3||acvpu == 3){
  risk = 'Low-medium';
  response = 'Urgent review by a ward-based doctor, to decide change in frequency of clinical monitoring or escalation of care';
  freq = 'Every 1 hour';
} 
  else{
  risk = 'Low';
  response = 'Assessment by a competent registered nurse or equivalent, to decide change in frequency of clinical monitoring or escalation of care';
	if(u == 0){
    freq = 'Every 12 hours';
    }
  	else{
    freq = 'Every 4-6 hours'; 
	}
  }
}

else if (u >= 5 && u <= 6){
  risk = 'Medium';
  response = 'Urgent review by a ward-based doctor or acute team nurse, to decide if critical care team assessment is needed';
  freq = 'Every 1 hour';
}
else if (u >= 7){
  risk = 'High'
  response = 'Emergent assessment by a clinical team or critical care team and usually transfer to higher level of care'
  freq = 'Continuous monitoring';
} 

calc_output.push({
  name:'mini',
  value: u,
  value_text: 'points',
  message: risk + ' risk'
});
calc_output.push({
  name:'10083_NEWS2 Score_result (score)',
  value: u,
  value_text: 'points',
  message: 'National Early Warning Score'
});
calc_output.push({
  name:'10083_NEWS2 Score_result (interpretation)',
  value:risk,
  value_text: 'risk',
  message: response
});
calc_output.push({
  name:'10083_NEWS2 Score_result (freq)',
  value:freq,
  value_text: '',
  message: 'Frequency of monitoring'
});
