var calc_output = [];

var fever = parseFloat(fever)
var lymph = parseFloat(lymph)
var lymphocytes = parseFloat(lymphocytes)
var eos = parseFloat(eos)
var rash = parseFloat(rash)
var needtwo = parseFloat(needtwo)
var dress = parseFloat(dress)
var internal = parseFloat(internal)
var resolution = parseFloat(resolution)
var altdx = parseFloat(altdx)

var score = fever + lymph + lymphocytes + eos + rash + needtwo + dress + internal + resolution + altdx

var likelihood 

if (score < 2) {
	likelihood = 'No case'
}
else if (score == 2 || score == 3) {
	likelihood = 'Possible case'
}
else if (score == 4 || score == 5) {
	likelihood = 'Probable case'
}
else {
	likelihood = 'Definite case'
}

//results 
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: likelihood
    });
