calc.equation_logic_text:
var calc_output = [];

var immunosup = parseFloat(immunosup);
var temp = parseFloat(temp);
var organ = parseFloat(organ);
var cytopenia = parseFloat(cytopenia);
var ferritin = parseFloat(ferritin);
var triglyc = parseFloat(triglyc);
var fibro = parseFloat(fibro);
var serum = parseFloat(serum);
var hemophago = parseFloat(hemophago);

var score = immunosup + temp + organ + cytopenia + ferritin + triglyc + fibro + serum + hemophago;

var prob;

if (score <= 90){
	prob = '<1'
}
else if (score >= 91 && score <= 100){
	prob = '~1'
}
else if (score >= 101 && score <= 110){
	prob = '1-3'
}
else if (score >= 111 && score <= 120){
	prob = '3-5'
}
else if (score >= 121 && score <= 130){
	prob = '5-9'
}	
else if (score >= 131 && score <= 140){
	prob = '9-16'
}
else if (score >= 141 && score <= 150){
	prob = '16-25'
}
else if (score >= 151 && score <= 160){
	prob = '25-40'
}
else if (score >= 161 && score <= 170){
	prob = '40-54'
}
else if (score >= 171 && score <= 180){
	prob = '54-70'
}
else if (score >= 181 && score <= 190){
	prob = '70-80'
}
else if (score >= 191 && score <= 200){
	prob = '80-88'
}
else if (score >= 201 && score <= 210){
	prob = '88-93'
}
else if (score >= 211 && score <= 220){
	prob = '93-96'
}
else if (score >= 221 && score <= 230){
	prob = '96-98'
}
else if (score >= 231 && score <= 240){
	prob = '98-99'
}
else{
	prob = '>99'
}

calc_output.push({
	name: 'mini',
	value: score,
	value_text: 'points',
	message: prob + '% probability of hemophagocytic syndrome'  
	});
calc_output.push({
    name: 'score',
    value: score,
    value_text: 'points',
    message: 'HScore'
    });
calc_output.push({
	name: 'prob',
	value: prob, 
	value_text: '%',
	message: 'Probability of hemophagocytic syndrome'
	});

if (score >=169) {
	calc_output.push({
		name: 'scorecutoff',
		value: '',
		value_text: '',
		message: 'Optimal cutoff is 169 (accurately classifies 90% of patients)'
		});	
}
