calc.equation_logic_text:
var calc_output = [];

var a = parseFloat(age);
var b = parseFloat(world);
var c = parseFloat(orient);
var d = parseFloat(nursing);

var score = a + b + c + d; 

var risk1;

if (score == 0) {
	risk1 = '2';
}
else if (score == 1) { 
	risk1 = '4';
}
else if (score == 2) { 
	risk1 = '14';
}
else if (score == 3) { 
	risk1 = '20';
}
else if (score == 4) { 
	risk1 = '64';
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk1 + '% risk of delirium'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'AWOL Score'   
    });
calc_output.push({
    name: 'risk1',
    value: risk1, 
    value_text: '%',
    message: 'Risk of delirium during hospitalization'
});
