calc.equation_logic_text:
var calc_output = [];

var wt = parseFloat(wt);
var ht = parseFloat(ht);
var arterialsat = parseFloat(arterialsat) / 100;
var venoussat = parseFloat(venoussat) / 100;
var hr = parseFloat(hr);
var age = parseFloat(age);

if (UOMSYSTEM == true) {
  var hb = parseFloat(hb);
}
if (UOMSYSTEM == false) {
  var hb = parseFloat(hb)/10;
}


var bsa = ((wt * ht) / 3600);
bsa = Math.pow(bsa, 0.5);

var vmax = 125 * bsa;
if (age == 1) {
  vmax = 110 * bsa
}

var cardiacoutput = (vmax / ((arterialsat - venoussat) * hb * 13.4)); 

var index = cardiacoutput  / bsa;
var volume = (cardiacoutput * 1000) / hr; 

calc_output.push({
    name: 'mini',
    value: cardiacoutput.toFixed(1),
    value_text: 'L/min',
    message: 'Cardiac output'
    });
calc_output.push({
    name: '10095_Fick Cardiac Output_result (output)',
    value: cardiacoutput.toFixed(1),
    value_text: 'L/min',
    message: 'Cardiac output'
    });
calc_output.push({
    name: '10095_Fick Cardiac Output_result (index)',
    value: index.toFixed(1),
    value_text: 'L/min/m\u00B2',
    message: 'Cardiac index'
    });
calc_output.push({
    name: '10095_Fick Cardiac Output_result (volume)',
    value: volume.toFixed(0),
    value_text: 'mL/beat',
    message: 'Stroke volume'
    });
