calc.equation_logic_text:
var calc_output = [];

var a = parseFloat(obesity);
var b = parseFloat(dnr);
var c = parseFloat(bleeding);
var d = parseFloat(loss);
var e = parseFloat(steroids);
var f = parseFloat(cardiac);
var g = parseFloat(age);
var h = parseFloat(cancer);
var i = parseFloat(pfs);
var j = parseFloat(sepsis);
var k = parseFloat(inpatient);
var l = parseFloat(pulmonary);
var m = parseFloat(renal);
var n = parseFloat(liver);

var score = a + b + c + d + e + f + g + h + i + j + k + l + m + n; 

var risk1;

if (score >= -1 && score <= 5) {
	risk1 = '0.1';
}
else if (score > 5 && score < 6) { 
	risk1 = '0.1-0.6';
}
else if (score >= 6 && score <= 10) { 
	risk1 = '0.6';
}
else if (score > 10 && score <  11) { 
	risk1 = '0.6-2.4';
}
else if (score >= 11 && score <= 15) { 
	risk1 = '2.4';
}
else if (score > 15 && score <  16) { 
	risk1 = '2.4-12.6';
}
else if (score >= 16 && score <= 20) { 
	risk1 = '12.6';
}
else if (score > 20 && score <  21) { 
	risk1 = '12.6-37.5';
}
else if (score >= 21 && score <= 25) { 
	risk1 = '37.5';
}
else if (score > 25 && score < 26) { 
	risk1 = '37.5-40.0';
}
else if (score >= 26 && score <= 30) { 
	risk1 = '40.0';
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk1 + '% risk of perioperative mortality'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'PMP Score'   
    });
calc_output.push({
    name: 'risk1',
    value: risk1, 
    value_text: '%',
    message: 'Risk of perioperative mortality'
});
