calc.equation_logic_text:
var calc_output = [];

var cyst = parseFloat(cyst);
var lesions = parseFloat(lesions);
var areas = parseFloat(areas);
var ascites = parseFloat(ascites);
var intra = parseFloat(intra);
var meno = parseFloat(meno);
var ca = parseFloat(ca);

var ultrascore = cyst + lesions + areas + ascites + intra; 

if(ultrascore == 0 || ultrascore == 1){
  ultrascore = 1;
}
else{ultrascore = 3;}

var score = ultrascore*meno*ca;

score = score.toFixed(0);

var risk1;
var msg;

if (score <= 200) {
	risk1 = 'Low';
  	msg = '';
}
else if (score > 200) { 
	risk1 = 'High';
  	msg = '71% sensitive and 92% specific for ovarian cancer';
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk1 + ' risk of malignancy'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'RMI'   
    });
calc_output.push({
    name: 'risk1',
    value: risk1, 
    value_text: 'risk of malignancy',
    message: msg
});
