calc.equation_logic_text:
var calc_output = [];

var a = parseFloat(gender);
var b = parseFloat(smoking);
var c = parseFloat(history);
var d = parseFloat(opiods);

var score = a + b + c + d; 

var risk1;

if (score == 0) {
	risk1 = '10';
}
else if (score == 1) { 
	risk1 = '21';
}
else if (score == 2) { 
	risk1 = '39';
}
else if (score == 3) { 
	risk1 = '61';
}
else if (score == 4) { 
	risk1 = '79';
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk1 + '% 24-hour risk of PONV'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'Apfel Score'   
    });
calc_output.push({
    name: 'risk1',
    value: risk1, 
    value_text: '%',
    message: '24-hour risk of PONV'
});
