calc.equation_logic_text:
var calc_output = [];

var ulcer = parseFloat(ulcer);
var calf = parseFloat(calf);
var ve = parseFloat(ve);
var red = parseFloat(red);
var hyper = parseFloat(hyper);
var skin = parseFloat(skin);
var pe = parseFloat(pe);
var pru = parseFloat(pru);
var pa = parseFloat(pa);
var heavy = parseFloat(heavy);
var cramps = parseFloat(cramps);
var pain = parseFloat(pain);

var score = calf + ve + red + hyper + skin + pe + pru + pa + heavy + cramps + pain;
if (ulcer == 15 && score < 15) {
  score = 15;
} else {
  score = score;
}

var pts;
var risk;
var msg;

if (score >= 0 && score <= 4) {
  risk = 'PTS absent';
  msg = 'PTS diagnosis';
} else if (score >=5 && score <= 9) {
  risk = 'Mild PTS';
  msg = 'PTS severity';
} else if (score >= 10 && score <= 14) {
  risk = 'Moderate PTS';
  msg = 'PTS severity';
} else if (score >= 15) {
  risk = 'Severe PTS';
  msg = 'PTS severity';
}

//results
calc_output.push({
    name: 'mini',
    value: score + ' points',
    value_text: '',
    message: risk
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'Villalta Score'   
});
calc_output.push({
    name: 'risk1',
    value: risk, 
    value_text: '',
    message: msg
});
