calc.equation_logic_text:
//outputs
var calc_output = [];

//user inputs
var haq = parseFloat(haq1) + parseFloat(haq2) + parseFloat(haq3) + parseFloat(haq4) + parseFloat(haq5) + parseFloat(haq6) + parseFloat(haq7) + parseFloat(haq8) + parseFloat(haq9) + parseFloat(haq10);
haq = haq/10;
var pain = parseFloat(pain);
var global = parseFloat(global);
var risk;
var msg;

//final score
var score = (haq*3.33) + pain + global;
score = score/3;
score = score.toFixed(2);

//disease activity
if (score <= 0.25 && score >= 0) {
	risk = 'Remission';
  	msg = risk;
} else if (score <= 3.70 && score >= 0.26) {
	risk = 'Low/minimal';
  	msg = risk + " disease activity";
} else if (score <= 7.99 && score >= 3.71) {
	risk = 'Moderate';
  	msg = risk + " disease activity";
} else {
	risk = 'High/severe';
  	msg = risk + " disease activity";
}

//result for user 
calc_output.push({
	name: 'mini',
	value: score,
	value_text: 'points',
	message: msg
});
calc_output.push({
	name: 'group',
	value: score,
	value_text: 'points',
	message: 'PAS II'
});
calc_output.push({
	name: 'ppf',
	value: risk,
	value_text: '',
	message: 'Disease activity<br/><br/><guideline id = "10123">ACR guideline</guideline>'
});
