calc.equation_logic_text:
var cardio = cardio || 0;
var neuro = neuro || 0;
var resp = resp || 0;
var renal = renal || 0;
var hep = hep || 0;
var hem = hem || 0;
var wbc18 = wbc18 || 0;
var mass18 = mass18 || 0;
var duration = duration || 0;
var local = local || 0;

var calc_output = [];

var parta = parseFloat(sign) + parseFloat(mass);
var partb = parseFloat(fever) + parseFloat(crp) + parseFloat(wbc);
var partc = parseFloat(image);
var grade3 = cardio + neuro + resp + renal + hep + hem;
var grade2 = wbc18 + mass18 + duration + local;

var diag, severity, risk, grade, msg;

if ((parta > 0 && partb > 0 && partc == 0) || (parta > 0 && partb == 0 && partc > 0)) {
  diag = 'Suspected';
  if (grade3 > 0) {
    severity = 'Severe';
    grade = 'III';
    risk = '5.4';
  } else if (grade2 > 0) {
    severity = 'Moderate';
    risk = '0.8';
    grade = 'II';
  } else { 
    severity = 'Mild';
    risk = '1.1';
    grade = 'I';
  }
  msg = 'Diagnosis of acute cholecystitis';
} else if (parta > 0 && partb > 0 && partc > 0) {
  diag = 'Definite';
  if (grade3 > 0) {
    severity = 'Severe';
    grade = 'III';
    risk = '5.4';
  } else if (grade2 > 0) {
    severity = 'Moderate';
    risk = '0.8';
    grade = 'II';
  } else { 
    severity = 'Mild';
    risk = '1.1';
    grade = 'I';
  }
  msg = 'Diagnosis of acute cholecystitis';
} else {
  diag = '';
  msg = 'Entry criteria not met for acute cholecystitis diagnosis';
}

//results
if(parta > 0 && (partb > 0 || partc > 0)){
calc_output.push({
    name: 'mini',
    value: diag,
    value_text: '',
    message: 'Grade ' + grade
});
} else {
calc_output.push({
    name: 'mini',
    value: '',
    value_text: '',
    message: msg
});}
calc_output.push({
    name: 'diag',
    value: diag,
    value_text: '',
    message: msg
});
if(parta > 0 && (partb > 0 || partc > 0)){
calc_output.push({
    name: 'grade',
    value: 'Grade ' + grade,
    value_text: '',
    message: severity + ' acute cholecystitis<br/><br/>' + risk + '% 30-day mortality' 
});
}
