calc.equation_logic_text:
var calc_output = [];

var inr  = parseFloat(inr);
var age  = parseFloat(age);
var cr   = parseFloat(cr);
var bili = parseFloat(bili);

if (UOMSYSTEM == false) {
  bili = bili/17.104;
  cr = cr/88.4;
}

var score = (age*0.1) + (bili*0.08) + (cr*0.3) + (inr*0.8);
score = score.toFixed(2);

var risk1, risk2, risk3;

if (score < 6.71) {
  risk1 = 'Low';
  risk2 = '100';
  risk3 = '97.1';
} else if (score >= 6.71 && score < 9) {
  risk1 = 'Intermediate';
  risk2 = '70';
  risk3 = '64.3';
} else if (score >= 9) {
  risk1 = 'High';
  risk2 = '25';
  risk3 = '33.3';
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk1 + ' risk'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'ABIC Score'
});
calc_output.push({
    name: 'Score',
    value: risk1,
    value_text: 'risk',
    message: risk2 + '% survival at 90 days<br/>' + risk3 + '% survival at 1 year' 
});
