calc.equation_logic_text:
var cardio = cardio || 0;
var neuro = neuro || 0;
var resp = resp || 0;
var renal = renal || 0;
var hepatic = hepatic || 0;
var hema = hema || 0;
var wbc = wbc || 0;
var fever2 = fever2 || 0;
var age = age || 0;
var hyper = hyper || 0;
var hypo = hypo || 0;

var calc_output = [];

var parta = parseFloat(fever) + parseFloat(lab);
var partb = parseFloat(jaundice) + parseFloat(lab2);
var partc = parseFloat(bili) + parseFloat(evidence);
var grade3 = parseFloat(cardio) + parseFloat(neuro) + parseFloat(resp) + parseFloat(renal) + parseFloat(hepatic) + parseFloat(hema);
var grade2 = parseFloat(wbc) + parseFloat(fever2) + parseFloat(age) + parseFloat(hyper) + parseFloat(hypo);

var diag, severity, risk, grade;
var msg = 'Diagnosis of acute cholangitis';

if (parta > 0 && partb > 0 && partc > 0) {
  diag = 'Definite';
  if (grade3 > 0) {
    severity = 'Severe';
    grade = 'III';
    risk = 'Recommendation: initial treatment with antibiotics, urgent biliary drainage, appropriate respiratory/circulatory management';
  } else if (grade2 > 1) {
    severity = 'Moderate';
    risk = 'Recommendation: antibiotics and general supportive care; early endoscopic or percutaneous transhepatic biliary drainage is indicated';
    grade = 'II';
  } else if (grade2 <= 1 && grade3 == 0) { 
    severity = 'Mild';
    risk = 'Recommendation: antibiotics and general supportive care; consider biliary drainage if no response to initial treatment';
    grade = 'I';
  } else {
    grade = "not assigned";
    risk = "";
    severity = "No grade for";
  }  
} else if (parta > 0 && (partb > 0 || partc > 0)) {
  diag = 'Suspected';
  if (grade3 > 0) {
    severity = 'Severe';
    grade = 'III';
    risk = 'Recommendation: initial treatment with antibiotics, urgent biliary drainage, appropriate respiratory/circulatory management';
  } else if (grade2 > 1) {
    severity = 'Moderate';
    risk = 'Recommendation: antibiotics and general supportive care; early endoscopic or percutaneous transhepatic biliary drainage is indicated';
    grade = 'II';
  } else if (grade2 <= 1 && grade3 == 0) { 
    severity = 'Mild';
    risk = 'Recommendation: antibiotics and general supportive care; consider biliary drainage if no response to initial treatment';
    grade = 'I';
  } else {
    grade = "not assigned";
    risk = "";
    severity = "No grade for";
  }
} else {
  diag = '';
  msg = 'Entry criteria not met for acute cholangitis diagnosis';
  grade = 'not assigned';
}

//results
if(parta > 0 && (partb > 0 || partc > 0)){
calc_output.push({
    name: 'mini',
    value: diag,
    value_text: '',
    message: 'Grade ' + grade
});
} else {
calc_output.push({
    name: 'mini',
    value: '',
    value_text: '',
    message: msg
});}
calc_output.push({
    name: 'diag',
    value: diag,
    value_text: '',
    message: msg
});
if(parta > 0 && (partb > 0 || partc > 0)){
calc_output.push({
    name: 'grade',
    value: 'Grade ' + grade,
    value_text: '',
    message: severity + ' acute cholangitis<br/><br/>' + risk 
});
}
