calc.equation_logic_text:
//vars
var calc_output = [];
var peak = parseFloat(peak);
var pa = parseFloat(pa);
var cardiac = parseFloat(cardiac);
var bicarb = parseFloat(bicarb);
var nitric = parseFloat(nitric);
var ecmo = parseFloat(ecmo);
var acute = parseFloat(acute);
var cns = parseFloat(cns);
var diag = parseFloat(diag);
var status = parseFloat(status);
var venti = parseFloat(venti);
var age = parseFloat(age);

//score
var score = peak + pa + cardiac + bicarb + nitric + ecmo + acute + cns + diag + status + venti + age;

//risk
var risk, surv;
if (score >= 6) {
  risk = 'I';
  surv = '92';
} else if (score >= 3 && score <= 5) {
  risk = 'II';
  surv = '76';
} else if (score >= -1 && score <= 2) {
  risk = 'III';
  surv = '57';
} else if (score >= -5 && score <= -2) {
  risk = 'IV';
  surv = '33';
} else if (score <= -6) {
  risk = 'V';
  surv = '18';
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: surv + '% survival' 
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'RESP Score'
});
calc_output.push({
    name: 'Score',
    value: 'Class ' + risk,
    value_text: '',
    message: 'Risk class' 
});
calc_output.push({
    name: 'Score',
    value: surv,
    value_text: '%',
    message: 'In-hospital survival' 
});
