calc.equation_logic_text:
var calc_output = [];
var na = parseFloat(na);
var cr = parseFloat(cr);
var bili = parseFloat(bili);
var inr = parseFloat(inr);

if (UOMSYSTEM == true) {
  cr = cr*88.4;
  bili = bili*17.1;
}  

var ukeld = 5.395*Math.log(inr) + 1.485*Math.log(cr) + 3.13*Math.log(bili) - 81.565*Math.log(na) + 435;

ukeld = ukeld.toFixed(0);

var rec, risk;

if (ukeld < 49) {
  risk = '≤9';
  rec = 'Not eligible';
} else if (ukeld >= 49) {
  risk = '>9';
  rec = 'Eligible';
}

//results
calc_output.push({
    name: 'mini',
    value: ukeld,
    value_text: 'points',
    message: rec + ' for transplant listing in UK'
});
calc_output.push({
    name: 'Score',
    value: ukeld,
    value_text: 'points',
    message: 'UKELD Score'   
});
calc_output.push({
    name: 'risk1',
    value: rec, 
    value_text: '',
    message: rec + ' for transplant listing in UK (' + risk + '% mortality at 1 year without transplant)'
});
