calc.equation_logic_text:
//define vars
var calc_output = [];
var energy = parseFloat(energy);
var sleep = parseFloat(sleep);
var confidence = parseFloat(confidence);
var activities = parseFloat(activities);
var breath = parseFloat(breath);
var chest = parseFloat(chest);
var ph = parseFloat(ph);
var cough = parseFloat(cough);

//score
var score = energy + sleep + confidence + activities + breath + chest + ph + cough;

//interpretation
var risk, rec;

if (score >= 0 && score <= 10) {
	risk = "Low";
	rec = "smoking cessation, preventive care, and reduced exposure to exacerbation risk factors; consider LAMA and rescue inhalers";
} else if (score >= 11 && score <= 20) {
	risk = "Medium";
	rec = "smoking cessation, preventive care, reduced exposure to exacerbation risk factors, and LAMA and rescue inhalers; consider ICS and/or LABA, referrals for pulmonary rehab, and possible lung transplant evaluation";
} else if (score >= 21 && score <= 30) {
	risk = "High";
	rec = "smoking cessation, preventive care, reduced exposure to exacerbation risk factors, ICS/LABA/LAMA therapy, referrals for pulmonary rehab, possible lung transplant evaluation, and O₂ supplementation";
} else if (score >= 31 && score <= 40) {
	risk = "Very high";
	rec = "smoking cessation, preventive care, reduced exposure to exacerbation risk factors, ICS/LABA/LAMA therapy, referrals for pulmonary rehab, possible lung transplant evaluation, and O₂ supplementation";
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk + ' health impact'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'CAT Score'   
});
calc_output.push({
    name: 'risk1',
    value: risk, 
    value_text: '',
    message: 'Health impact'
});
calc_output.push({
    name: 'risk1',
    value: '', 
    value_text: '',
    message: 'Recommend ' + rec
});
