calc.equation_logic_text:
var calc_output = [];
var age = parseFloat(age); 
var sex = parseFloat(sex); 
var sweating = parseFloat(sweating); 
var ecg = parseFloat(ecg);
var pain = parseFloat(pain); 
var vomit = parseFloat(vomit); 
var bp = parseFloat(bp); 
var smoker = parseFloat(smoker);

var x = - (1.426 * sweating + 1.838 * ecg + 0.734 * pain + 0.996 * vomit + 1.353 * bp + 0.675 * smoker + 0.024 * age + 0.462 * sex - 4.416);

var score = 1 + Math.exp(x);

score = 1/score;

var percent = score*100;

percent = percent.toFixed(1);

var risk;

if (percent < 4.0) {
  risk = "Very low";
} else if (percent >= 4.0 && percent <= 6.9) {
  risk = "Low";
} else if (percent >= 7.0 && percent <= 49.9) {
  risk = "Moderate";
} else if (percent >= 50.0) {
  risk = "High";
}

//results
calc_output.push({
    name: 'mini',
    value: risk,
    value_text: 'risk',
    message: percent + '% risk of ACS or MACE in 30 days'
});
calc_output.push({
    name: 'Score',
    value: percent,
    value_text: '%',
    message: 'Risk of ACS or MACE in 30 days'   
});
calc_output.push({
    name: 'risk1',
    value: risk, 
    value_text: 'risk',
    message: 'Risk group'
});
