calc.equation_logic_text:
var calc_output = [];

var speech = parseFloat(speech);
var sal = parseFloat(sal);
var swallow = parseFloat(swallow);
var handwrite = parseFloat(handwrite);
var cutting1 = parseFloat(cutting1);
var cutting2 = parseFloat(cutting2);
var dress = parseFloat(dress);
var bed = parseFloat(bed);
var climb = parseFloat(climb);
var walk = parseFloat(walk);
var dys = parseFloat(dys);
var ortho = parseFloat(ortho);
var resp = parseFloat(resp);
var gastro = parseFloat(gastro);

if (gastro == 0) {
  var score = speech + sal + swallow + handwrite + cutting1 + dress + bed + walk + dys + ortho + resp + climb; 
} else if (gastro == 1) {
  var score = speech + sal + swallow + handwrite + cutting2 + dress + bed + walk + dys + ortho + resp + climb;  
}

var risk1;

if (score <= 15) {
	risk1 = '≤25';
} else if (score > 15 && score <= 20) { 
	risk1 = '~25-40';
} else if (score > 20 && score <= 25) { 
	risk1 = '~40-60';
} else if (score > 25 && score <= 30) { 
	risk1 = '~60-70';
} else if (score > 30 && score <= 35) { 
	risk1 = '~70-80';
} else if (score > 35 && score <= 40) { 
	risk1 = '~80-90';
} else if (score > 40) { 
	risk1 = '>90';
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk1 + '% 9-month survival'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'ALSFRS-R'   
    });
calc_output.push({
    name: 'risk1',
    value: risk1, 
    value_text: '%',
    message: '9-month survival'
});
