var dosage_1 = parseFloat(dosage_1) || 0;
var dosage_2 = parseFloat(dosage_2) || 0;
var dosage_3 = parseFloat(dosage_3) || 0;
var dosage2_1 = parseFloat(dosage2_1) || 0;
var dosage2_2 = parseFloat(dosage2_2) || 0;
