var drug2 = parseFloat(drug2) || 0;
var drug3 = parseFloat(drug3) || 0;
var dosage;

if (drug == 1) {
  dosage = dosage_3;
} else if (drug == 10) {
  dosage = dosage_2;
} else {
  dosage = dosage_1;
}

var dosage2;

if (drug2 == 1) {
  dosage2 = dosage2_3;
} else if (drug2 == 10) {
  dosage2 = dosage2_2;
} else {
  dosage2 = dosage2_1;
}

var dosage3;

if (drug3 == 1) {
  dosage3 = dosage3_3;
} else if (drug3 == 10) {
  dosage3 = dosage3_2;
} else {
  dosage3 = dosage3_1;
}

var names = ['codeine',
'fentanyl patch',
'hydrocodone',
'hydromorphone',
'methadone',
'morphine',
'oxycodone',
'oxymorphone',
'tramadol',
'tapentadol',
'fentanyl buccal or sublingual tablets'];


var conversion = [0.15,
2.4,
1,
4,
4,
1,
1.5,
3,
0.1,
0.4,
0.13];

var convert = conversion[drug];
var convert2 = conversion[drug2];
var convert3 = conversion[drug3];

if (drug == 4) {
  var i = dosage*amount;
  if (i > 0 && i <= 20) {
    convert = 4;
  } else if (i > 2 && i <= 40) {
    convert = 8;
  } else if (i >= 40 && i <= 60) {
    convert = 10;
  } else if (i > 60) {
    convert = 12;
  }
}
if (drug2 == 4) {
  var i = dosage2*amount2;
  if (i > 0 && i <= 20) {
