calc.equation_logic_text:
var calc_output = [];

var manicsymptom1 = parseFloat(manicsymptom1);
var manicsymptom2 = parseFloat(manicsymptom2);
var manicsymptom3 = parseFloat(manicsymptom3);
var manicsymptom4 = parseFloat(manicsymptom4);
var manicsymptom5 = parseFloat(manicsymptom5);
var manicsymptom6 = parseFloat(manicsymptom6);
var manicsymptom7 = parseFloat(manicsymptom7);
var manic1 = parseFloat(manic1);
var manic2 = parseFloat(manic2);
var manic3 = parseFloat(manic3);
var hypomanicsymptom1 = parseFloat(hypomanicsymptom1);
var hypomanicsymptom2 = parseFloat(hypomanicsymptom2);
var hypomanicsymptom3 = parseFloat(hypomanicsymptom3);
var hypomanicsymptom4 = parseFloat(hypomanicsymptom4);
var hypomanicsymptom5 = parseFloat(hypomanicsymptom5);
var hypomanicsymptom6 = parseFloat(hypomanicsymptom6);
var hypomanicsymptom7 = parseFloat(hypomanicsymptom7);
var hypomanic1 = parseFloat(hypomanic1);
var hypomanic2 = parseFloat(hypomanic2);
var hypomanic3 = parseFloat(hypomanic3);
var hypomanic4 = parseFloat(hypomanic4);
var hypomanic5 = parseFloat(hypomanic5);
var mood = parseFloat(mood);
var loss = parseFloat(loss);
var weight = parseFloat(weight);
var insomnia = parseFloat(insomnia);
var psycho = parseFloat(psycho);
var fatigue = parseFloat(fatigue);
var guilt = parseFloat(guilt);
var concentration = parseFloat(concentration);
var suicide = parseFloat(suicide);
var two = parseFloat(two);
var three = parseFloat(three);

var manic_diag, hypomanic_diag, depressed_diag, diag, mini_diag;

var manic = manic1 + manic2 + manic3;
var manic_symp = manicsymptom1 +
manicsymptom2 +
manicsymptom3 +
manicsymptom4 +
manicsymptom5 +
manicsymptom6 +
manicsymptom7;

if (manic == 3 && manic_symp >= 3) {
	manic_diag = 1;
} else {
	manic_diag = 0;
}

var hypomanic = hypomanic1 + hypomanic2 + hypomanic3 + hypomanic4 + hypomanic5;

var hypomanic_symp = hypomanicsymptom1 + hypomanicsymptom2 + hypomanicsymptom3 + hypomanicsymptom4 + hypomanicsymptom5 + hypomanicsymptom6 + hypomanicsymptom7;

if (hypomanic == 5 && hypomanic_symp >= 3) {
	hypomanic_diag = 1;
} else {
	hypomanic_diag = 0;
}

var depressed = two + three;
var depressed_symp = mood + loss + weight + insomnia + psycho + fatigue + guilt + concentration + suicide;

if (depressed == 2 && depressed_symp >= 5 && (mood + loss > 0)) {
	depressed_diag = 1;
} else {
	depressed_diag = 0;
}

if (manic_diag == 1) {
	mini_diag = 'bipolar I disorder';
	diag = 'Positive';
} else if (hypomanic_diag == 1 && manic_diag == 0 && depressed_diag == 1) {
	mini_diag = 'bipolar II disorder';
	diag = 'Positive';
} else {
	mini_diag = 'bipolar I/II disorder';
	diag = 'Not positive';
}

//results
calc_output.push({
    name: 'mini',
    value: diag,
    value_text: '',
    message: diag + ' for ' + mini_diag
});
calc_output.push({
    name: 'Score',
    value: diag,
    value_text: '',
    message: diag + ' for ' + mini_diag + ' by DSM-5 Criteria'   
});
