calc.equation_logic_text:
var calc_output = [];
var copd = parseFloat(copd);
var procedure = parseFloat(procedure);
var smoking = parseFloat(smoking);
var sepsis = parseFloat(sepsis);
var class1 = parseFloat(class1);
var status = parseFloat(status);
var age = parseFloat(age);

var b = -2.8977 + copd + status + class1 + sepsis + smoking + procedure + age*0.0144;

var risk = Math.exp(b)/(1 + Math.exp(b)) * 100;

calc_output.push({
   name: 'mini',
   value: risk.toFixed(1),
   value_text: '%',
   message: 'Risk of postoperative pneumonia'
});
calc_output.push({
   name: 'Gupta score',
   value: risk.toFixed(1),
   value_text: '%',
   message: 'Risk of postoperative pneumonia'
});
