calc.equation_logic_text:
var calc_output = [];
var platelet = parseFloat(platelet); 
var reticulocyte = parseFloat(reticulocyte);     
var cancer = parseFloat(cancer); 
var history = parseFloat(history); 
var mcv = parseFloat(mcv); 
var inr = parseFloat(inr); 
var cr = parseFloat(cr); 

var score = platelet + reticulocyte + cancer + history + mcv + inr + cr;

var risk1, risk2;

if (score >= 0 && score <=4) {
	risk1 = 'Low';
	risk2 = 0;
} else if (score == 5) {
	risk1 = 'Intermediate';
	risk2 = 6;
} else if (score >= 6) {
	risk1 = 'High';
	risk2 = 72;
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk1 + ' risk'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'PLASMIC Score'   
    });
calc_output.push({
    name: 'risk1',
    value: risk1, 
    value_text: 'risk',
    message: 'Risk group'
});
calc_output.push({
    name: 'risk2',
    value: risk2, 
    value_text: '%',
    message: 'Risk of severe ADAMTS13 deficiency (defined as ADAMTS13 activity level <15%)'
});
