calc.equation_logic_text:
var calc_output = [];

var platelet = parseFloat(platelet);
var marker = parseFloat(marker);
var pt = parseFloat(pt);
var fib = parseFloat(fib);

var score = platelet + marker + pt + fib;

var diag;
if (score >= 5) {
  diag = 'Compatible with overt DIC; treat for DIC as appropriate and repeat scoring daily';
} else {
  diag = 'Not suggestive of overt DIC, may be non-overt DIC; repeat within next 1-2 days and manage clinically as appropriate';
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: diag
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: diag   
});
