calc.equation_logic_text:
var calc_output = [];
var history = parseFloat(history);
var age = parseFloat(age);
var chest = parseFloat(chest);
var pain = parseFloat(pain);
var phys = parseFloat(phys);
var pressure = parseFloat(pressure);

var score = history + age + chest + pain  + phys + pressure;

var diag, prob,msg; 

if (score <= 1) {
  diag = 'Low';
  prob = '2.1';
  msg = 'Diagnostic workup can likely be done non-urgently if patient is otherwise stable'
} else {
  diag = 'Not low';
  prob = '43.0';
  msg = 'Consider more urgent diagnostic testing'
}

//results
calc_output.push({
    name: 'mini',
    value: diag,
    value_text: 'risk',
    message: prob + '% probability of CAD'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'INTERCHEST Score'   
});
calc_output.push({
    name: 'risk1',
    value: diag, 
    value_text: 'risk',
    message: 'CAD risk'
});
calc_output.push({
    name: 'risk1',
    value: prob, 
    value_text: '%',
    message: 'Probability of CAD' + '<br/><br/>' + msg
});
