calc.equation_logic_text:
var calc_output = [];

var excite = parseFloat(excite);
var immobile = parseFloat(immobile);
var mute = parseFloat(mute);
var stare = parseFloat(stare);
var posture = parseFloat(posture);
var grimace = parseFloat(grimace);
var echo = parseFloat(echo);
var stereo = parseFloat(stereo);
var manner = parseFloat(manner);
var verb = parseFloat(verb);
var rigid = parseFloat(rigid);
var negative = parseFloat(negative);
var waxy = parseFloat(waxy);
var withdrawal = parseFloat(withdrawal);
var impulse = parseFloat(impulse) || 0;
var auto = parseFloat(auto) || 0;
var mit = parseFloat(mit) || 0;
var geg = parseFloat(geg) || 0;
var amb = parseFloat(amb) || 0;
var grasp = parseFloat(grasp) || 0;
var preserve = parseFloat(preserve) || 0;
var combat = parseFloat(combat) || 0;
var ab = parseFloat(ab) || 0;

var mini = 'Higher scores indicate greater severity of catatonia';
var msg = 'Bush-Francis Catatonia Rating Scale<br/><br/>Higher scores indicate greater severity of catatonia';
var pt = 'points';
if ([excite,immobile,mute,stare,posture,grimace,echo,stereo,manner,verb,rigid,negative,waxy,withdrawal].filter(val => val > 0).length >= 2) {
  var score = excite + immobile + mute + stare + posture + grimace + echo + stereo + manner + verb + rigid + negative + waxy + withdrawal + impulse + auto + mit + geg + amb + grasp + preserve + combat + ab;
} else {
  score = ''
  msg = 'Not positive for catatonia as per Bush-Francis Catatonia Screening'
  pt = ''
  mini = 'Not positive for catatonia as per Bush-Francis Catatonia Screening'
}



//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: pt,
    message: mini
});
calc_output.push({
    name: 'TNM RCC',
    value: score,
    value_text: pt,
    message: msg
});
