calc.equation_logic_text:
var calc_output=[];

var age_raw = parseFloat(age);
var sex = parseFloat(sex);
var cci_raw = parseFloat(diabetes)+parseFloat(cpd)+parseFloat(kidney)+parseFloat(pvd)+parseFloat(chf)+parseFloat(dementia)+parseFloat(cancer)+parseFloat(stroke)+parseFloat(liver)+parseFloat(heart_attack)+parseFloat(ulcer)+parseFloat(paralysis)+parseFloat(aids)+parseFloat(rheum);

if (parseFloat(pvd) ==1 || parseFloat(heart_attack) ==1){
  var history=0.267265312;
}
if (parseFloat(pvd) ==0 && parseFloat(heart_attack) ==0){
  var history=0;
}

if (age_raw >=20 && age_raw <=49){
  age = -2.228678713;
}
if (age_raw >=50 && age_raw <=54){
  age = 0;
}
if (age_raw >=55 && age_raw <=59){
  age = 0.400599289;
}
if (age_raw >=60 && age_raw <=64){
  age = 0.941322019;
}
if (age_raw >=65 && age_raw <=69){
  age = 1.295007128;
}
if (age_raw >=70 && age_raw <=74){
  age = 1.629533438;
}
if (age_raw >=75 && age_raw <=79){
  age = 1.763345763;
}
if (age_raw >=80 && age_raw <=89){
  age = 1.927443543;
}
if (age_raw >=90){
  age = 2.018752269;
}

if (age_raw <85){
	if (cci_raw==0){
  	var cci=0;
	}
	if (cci_raw>=1 && cci_raw<=3){
 	var cci=0.612122574;
	}
	if (cci_raw>=4 && cci_raw<=5){
	  var cci=0.82507284;
	}
	if (cci_raw>=6 && cci_raw<=9){
	  var cci=0.956099733;
	}
	if (cci_raw>=10){
	  var cci=1.395164653;
	}
}

if (age_raw >=85){
  var cci = 1.529325519;
}


var coefficient_sum = age + sex + cci + history - 4.216058062;
var or_calc = Math.exp(coefficient_sum);
var risk_raw = (or_calc/(1 + or_calc))*100;

var risk = risk_raw.toFixed(1);

var odds_raw = 1/(risk_raw/100);
var odds = odds_raw.toFixed(0);

calc_output.push({
    name: 'mini',
    value: risk,
	value_text:'% risk of death',
    message: ''
});
calc_output.push({
    name: 'VACO Index',
    value: risk,
    value_text:'% risk of death',
    message: ''
});
calc_output.push({
    name: 'Percentile',
    value: '1 in '+odds+' chance of dying',
    value_text:'',
    message: ''
});
