calc.equation_logic_text:
var calc_output=[];

var age = parseFloat(age);
if (age<50) {
  var age_points = -2;
} else if (age >= 50 && age <= 64) {
  var age_points = -1;
} else if (age>64) { 
   var age_points = 0;
}

var criteria= age_points + parseFloat(sex) + parseFloat(resp) + parseFloat(bpm) + parseFloat(chest) + parseFloat(estrogen) + parseFloat(vte) + parseFloat(sync) + parseFloat(imm) + parseFloat(pulse) + parseFloat(calf) + parseFloat(pediag);


if (criteria <0){
  var prob = '<2';
  var prob_msg = 'Very low CPP';
  var diag = 'PE can be ruled out';
}
if (criteria >=0 && criteria <=5){
  var prob = '2-20';
  var prob_msg = 'Low CPP';
  var diag = 'PE can be ruled out if D-dimer level <1.0 µg/mL';

}

if (criteria >=6 && criteria <=12){
  var prob = '20-65';
  var prob_msg = 'Moderate CPP';
  var diag = 'PE can be ruled out if D-dimer level <0.5 µg/mL OR <(age x 0.01) µg/mL';
}

if (criteria >=13){
  var prob = '>65';
  var prob_msg = 'High CPP';
  var diag = 'PE cannot be ruled out without imaging testing';
}


calc_output.push({
    name: 'mini',
    value: criteria,
    value_text:'points',
    message:prob_msg
});
calc_output.push({
    name: '4PEPS Score',
    value: criteria,
    value_text:'points',
    message:'4PEPS Score'
});
calc_output.push({
    name: 'Probability',
    value: prob,
    value_text:'%',
    message:prob_msg
});
calc_output.push({
    name: 'PE diagnosis',
    value: '',
    value_text:'',
    message:diag
});
