var calc_output = [];

var gest = parseFloat(gest);
var age = parseFloat(age);

if (UOMSYSTEM == true) {  //US units
    var tsb = parseFloat(tsb);
} else if (UOMSYSTEM == false) { //SI units
    var tsb = parseFloat(tsb)/88.4;
}

var aap;

if (age <=48) {
    aap = 15;
} else if (age <= 72) {
    aap = 18;
} else {
    aap = 20;
}

var score = 15*gest - 7*(age/24) - 4*(aap - tsb) + 50;

var risk;

if (score <=9) {
    risk = 0.3;
} else if (score <= 19) {
    risk = 1.8;
} else if (score <= 29) {
    risk = 6.1;
} else if (score <= 39) {
    risk = 17.4;
} else if (score <= 49) {
    risk = 25.8;
} else {
    risk = 40.5;
}

calc_output.push({
    name: 'mini',
    value: score.toFixed(1),
    value_text: 'points',
    message: risk + '% risk of rebound hyperbilirubinemia'
});
