calc.equation_logic_text:
var calc_output = [];
var u = parseFloat(age_range) + parseFloat(exudate_swelling) + parseFloat(lymph_nodes) + parseFloat(fever) + parseFloat(cough);
var msg, mini_msg;
if (u == -1) {
    msg = '1% - 2.5% likelihood of strep<br/><br/>No further testing nor antibiotics.';
    mini_msg = '1% - 2.5% '
}
if (u == 0) {
    msg = '1% - 2.5% likelihood of strep<br/><br/>No further testing nor antibiotics.';
    mini_msg = '1% - 2.5% '
}
if (u == 1) {
    msg = '5% - 10% likelihood of strep<br/><br/>No further testing nor antibiotics.';
    mini_msg = '5% - 10% '
}
if (u == 2) {
    msg = '11% - 17%<br/><br/>Optional rapid strep testing and/or culture.';
    mini_msg = '11% - 17% '
}
if (u == 3) {
    msg = '28% - 35%<br/><br/>Consider rapid strep testing and/or culture.';
    mini_msg = '28% - 35% '
}
if (u > 3) {
    msg = '51% - 53% likelihood of strep<br/><br/>Consider rapid strep testing and/or culture. Empiric antibiotics may be appropriate depending on the specific scenario.';
    mini_msg = '51% - 53% '
}
calc_output.push({
    name: 'mini',
    value: u.toFixed(0),
    value_text: 'points',
    message: mini_msg + 'Probability of Strep Pharyngitis'
});
calc_output.push({
    name: '104_Centor Score_result',
    value: u.toFixed(0),
    value_text: 'points',
    message: msg
});
