var calc_output = [];

// Declare variables

var delusions = parseFloat(delusions);
var disorg = parseFloat(disorg);
var hallu = parseFloat(hallu);
var excite = parseFloat(excite);
var grand = parseFloat(grand);
var sus = parseFloat(sus);
var hostility = parseFloat(hostility);
var blunt = parseFloat(blunt);
var withdrawal = parseFloat(withdrawal);
var rapport = parseFloat(rapport);
var apathy = parseFloat(apathy);
var abstract = parseFloat(abstract);
var flow = parseFloat(flow);
var stereotype = parseFloat(stereotype);
var concern = parseFloat(concern);
var anxiety = parseFloat(anxiety);
var guilt = parseFloat(guilt);
var tension = parseFloat(tension);
var manner = parseFloat(manner);
var depression = parseFloat(depression);
var motor = parseFloat(motor);
var uncooperative = parseFloat(uncooperative);
var unusual = parseFloat(unusual);
var disorientation = parseFloat(disorientation);
var attention = parseFloat(attention);
var insight = parseFloat(insight);
var volition = parseFloat(volition);
var impulse = parseFloat(impulse);
var preoccupation = parseFloat(preoccupation);
var social = parseFloat(social);

var pos = delusions + disorg + hallu + excite + grand + sus + hostility;
var neg = blunt + withdrawal + rapport + apathy + abstract + flow + stereotype;
var gen = concern + anxiety + guilt + tension + manner + depression + motor + uncooperative + unusual + disorientation + attention + insight + volition + impulse + preoccupation + social;
var score = pos + neg + gen;

var message;

if (score <= 57) {
  message = 'Not ill';
} else if (score <= 74) {
  message = 'Mildly ill';
} else if (score <= 94) {
  message = 'Moderately ill';
} else if (score <= 115) {
  message = 'Markedly ill';
} else {
  message = 'Severely ill';
}

calc_output.push({
  name: 'mini',
  value: score,
  value_text: 'point',
  message: message
});
calc_output.push({
  name: 'score',
  value: score,
  value_text: 'points',
  message: 'PANSS Score<br/><br/>Positive Scale: ' + pos + ' points<br/>Negative Scale: ' + neg + ' points<br/>General Psychopathology Scale: ' + gen + ' points'
});
calc_output.push({
  name: 'level',
  value: message,
  value_text: '',
  message: 'Schizophrenia severity'
});
