calc.equation_logic_text:
var calc_output = [];

// Declare variables

var speech = parseFloat(speech) || -2;
var gross_motor = parseFloat(gross_motor) || -2;
var eye = parseFloat(eye) || -2;
var target = parseFloat(target) || -2;
var fine_motor = parseFloat(fine_motor) || -2;

var sum = 0;

var max_score = 0;
var refuse = 0;

if (speech != -2 && speech != 5) {
  max_score = max_score + 4;
  if (speech == -1) {
    sum = sum;
  } else {
    sum = sum + speech;
  }
} else if (speech == 5) {
  refuse++;
}
if (gross_motor != -2 && gross_motor != 5) {
  max_score = max_score + 4;
  if (gross_motor == -1) {
    sum = sum;
  } else {
    sum = sum + gross_motor;
  }
} else if (gross_motor == 5) {
  refuse++;
}
if (eye != -2 && eye != 5) {
  max_score = max_score + 4;
  if (eye == -1) {
    sum = sum;
  } else {
    sum = sum + eye;
  }
} else if (eye == 5) {
  refuse++;
}
if (target != -2 && target != 5) {
  max_score = max_score + 4;
  if (target == -1) {
    sum = sum;
  } else {
    sum = sum + target;
  }
} else if (target == 5) {
  refuse++;
}
if (fine_motor != -2 && fine_motor != 5) {
  max_score = max_score + 4;
  if (fine_motor == -1) {
    sum = sum;
  } else {
    sum = sum + fine_motor;
  }
} else if (fine_motor == 5) {
  refuse++;
}

var score = sum/max_score;
var time = score/0.0616;

if (speech == -1) {
  speech = 0;
}
if (gross_motor == -1) {
  gross_motor = 0;
}
if (eye == -1) {
  eye = 0;
}
if (target == -1) {
  target = 0;
}
if (fine_motor == -1) {
  fine_motor = 0;
}

if (speech == 5) {
  speech = 'Patient refused';
}
if (gross_motor == 5) {
  gross_motor = 'Patient refused';
}
if (eye == 5) {
  eye = 'Patient refused';
}
if (target == 5) {
  target = 'Patient refused';
}
if (fine_motor == 5) {
  fine_motor = 'Patient refused';
}

var msg = '';

if (speech != -2) {
  msg = msg + '<br/>Speech: ' + speech;
}
if (gross_motor != -2) {
  msg = msg + '<br/>Gross motor: ' + gross_motor;
}
if (eye != -2) {
  msg = msg + '<br/>Eye movement: ' + eye;
}
if (target != -2) {
  msg = msg + '<br/>Coordination with target: ' + target;
}
if (fine_motor != -2) {
  msg = msg + '<br/>Fine motor: ' + fine_motor;
}

if (refuse > 1) {
  calc_output.push({
    name: 'mini',
    value: '',
    value_text: '',
    message: 'Unable to calculate score because patient refused too many tasks'
  });
  calc_output.push({
    name: 'refuse',
    value: refuse,
    value_text: 'refused tasks',
    message: 'Unable to calculate score because patient refused too many tasks'
  });
} else if (score > 0) {
  calc_output.push({
    name: 'mini',
    value: sum + '/' + max_score,
    value_text: '',
    message: 'HII Score'
  });
  calc_output.push({
    name: 'HII Score',
    value: sum + '/' + max_score,
    value_text: '',
    message: 'HII Score<br/>' + msg
  });
  calc_output.push({
    name: 'HII Index',
    value: score.toFixed(2),
    value_text: 'points',
    message: 'HII Index'
  });
  calc_output.push({
    name: 'time',
    value: Math.floor(time) + ' to ' + Math.ceil(time),
    value_text: 'hours',
    message: 'Estimated time to resolution of impairment'
  });
} else {
  calc_output.push({
    name: 'mini',
    value: sum + '/' + max_score,
    value_text: '',
    message: 'HII Score'
  });
  calc_output.push({
    name: 'HII Score',
    value: sum + '/' + max_score,
    value_text: '',
    message: 'HII Score<br/>' + msg
  });
  calc_output.push({
    name: 'HII Index',
    value: score.toFixed(2),
    value_text: 'points',
    message: 'HII Index'
  });
  calc_output.push({
    name: 'time',
    value: Math.floor(time) ,
    value_text: 'hours',
    message: 'Estimated time to resolution of impairment'
  });
}
