calc.equation_logic_text:
var calc_output = [];

var food = parseFloat(food);
var weight = parseFloat(weight);
var mobility = parseFloat(mobility);
var neuro = parseFloat(neuro);
var bmi = parseFloat(bmi);
var drugs = parseFloat(drugs);
var health = parseFloat(health);
var age = parseFloat(age);

var score = food + weight + mobility + neuro + bmi + drugs + health + age;

var rec;

if (score <= 14) {
    rec = 'Full geriatric evaluation recommended';
} else {
    rec = 'Full geriatric evaluation not necessary';
}

calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: rec
});
calc_output.push({
    name: 'score',
    value: score,
    value_text: 'points',
    message: rec
});
