calc.equation_logic_text:
var calc_output = [];

var h1 = parseFloat(h1);
var h2 = parseFloat(h2);
var h3 = parseFloat(h3);
var h4 = parseFloat(h4);
var hp = parseFloat(hp);
var hm = 0.1;

var easi_h = (h1 + h2 + h3  + h4)*hp*hm;

var u1 = parseFloat(u1);
var u2 = parseFloat(u2);
var u3 = parseFloat(u3);
var u4 = parseFloat(u4);
var up = parseFloat(up);
var um = 0.2;

var easi_u = (u1 + u2 + u3 + u4)*up*um;

var t1 = parseFloat(t1);
var t2 = parseFloat(t2);
var t3 = parseFloat(t3);
var t4 = parseFloat(t4);
var tp = parseFloat(tp);
var tm = 0.3;

var easi_t = (t1 + t2 + t3 + t4)*tp*tm;

var l1 = parseFloat(l1);
var l2 = parseFloat(l2);
var l3 = parseFloat(l3);
var l4 = parseFloat(l4);
var lp = parseFloat(lp);
var lm = 0.4;

var easi_l = (l1 + l2 + l3 + l4)*lp*lm;

var easi = easi_h + easi_u + easi_t + easi_l;

calc_output.push({
    name: 'mini',
    value: easi.toFixed(1),
    value_text: 'points',
    message: 'Total Eczema Area and Severity Index (EASI)<br/><br/>Head/neck EASI: ' + easi_h.toFixed(1) + ' points<br/>Upper limbs EASI: ' + easi_u.toFixed(1) + ' points<br/>Trunk EASI: ' + easi_t.toFixed(1) + ' points<br/>Lower limbs EASI: ' + easi_l.toFixed(1) + ' points'
});
calc_output.push({
    name: 'EASI',
    value: easi.toFixed(1),
    value_text: 'points',
    message: 'Total Eczema Area and Severity Index (EASI)<br/><br/>Head/neck EASI: ' + easi_h.toFixed(1) + ' points<br/>Upper limbs EASI: ' + easi_u.toFixed(1) + ' points<br/>Trunk EASI: ' + easi_t.toFixed(1) + ' points<br/>Lower limbs EASI: ' + easi_l.toFixed(1) + ' points'
});
