var calc_output = [];

var age = parseFloat(age);
var hem = parseFloat(hem);
var heart = parseFloat(heart);
var angina = parseFloat(angina);
var vascular = parseFloat(vascular);
var urgency = parseFloat(urgency);

var cvri = age + hem + heart + angina + vascular + urgency;

var risk;

if (cvri == 0) {
    risk = 0.3;
} else if (cvri == 1) {
    risk = 1.6;
} else if (cvri == 2) {
    risk = 5.6;
} else if (cvri == 3) {
    risk = 11;
} else {
    risk = 17.5;
}

calc_output.push({
    name: 'mini',
    value: cvri,
    value_text: 'points',
    message: 'AUB-HAS2'
});
