var score = population + hypertension + age + size + sah + site;

var risk;

if (score <= 2) {
    risk = 0.4;
} else if (score == 3) {
    risk = 0.7;
} else if (score == 4) {
    risk = 0.9;
} else if (score == 5) {
    risk = 1.3;
