    risk = 1.7;
} else if (score == 7) {
    risk = 2.4;
} else if (score == 8) {
    risk = 3.2;
} else if (score == 9) {
    risk = 4.3;
} else if (score == 10) {
    risk = 5.3;
} else if (score == 11) {
    risk = 7.2;
