calc.equation_logic_text:
var calc_output = [];

var age = parseFloat(age);
var gender = parseFloat(gender);
var gfr = parseFloat(gfr);
var cancer = parseFloat(cancer);
var rd = parseFloat(rd);
var cvc = parseFloat(cvc);
var icu = parseFloat(icu);
var inr = parseFloat(inr);
var platelet = parseFloat(platelet);
var bleeding = parseFloat(bleeding);
var ulcer = parseFloat(ulcer);

var score = age + gender + gfr + cancer + rd + cvc + icu + inr + platelet + bleeding + ulcer;

var risk;

if (score < 7) {
    risk = 'No increased risk of bleeding';
} else {
    risk = 'Increased risk of bleeding';
}

calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: 'IMPROVE Bleeding Risk Score<br/><br/>' + risk
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'IMPROVE Bleeding Risk Score<br/><br/>' + risk
});
