calc.equation_logic_text:
var calc_output = [];

var age = parseFloat(age);
var fractures = parseFloat(fractures);
var lung = parseFloat(lung);
var anticoagulant = parseFloat(anticoagulant);
var o2 = parseFloat(o2);

var age2 = Math.floor(age/10);

var score = age2 + fractures*3 + lung + anticoagulant + o2;

var risk;
if (score <= 10) {
    risk = '13 ± 6';
} else if (score <= 15) {
    risk = '29 ± 8';
} else if (score <= 20) {
    risk = '52 ± 8';
} else if (score <= 25) {
    risk = '70 ± 6';
} else if (score <= 30) {
    risk = '80 ± 6';
} else if (score >= 31) {
    risk = '88 ± 7';
}

calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk + '% risk of complications'
});
calc_output.push({
    name: 'STUMBL Score',
    value: score,
    value_text: 'points',
    message: 'STUMBL/Battle Score'
});
calc_output.push({
    name: 'Risk',
    value: risk,
    value_text: '%',
    message: 'Risk of complications'
})
