calc.equation_logic_text:
var calc_output = [];

var kps = parseFloat(kps);
var foci = parseFloat(foci);
var vert = parseFloat(vert);
var organs = parseFloat(organs);
var site = parseFloat(site);
var grade = parseFloat(grade);

var score = kps + foci + vert + organs + site + grade;

var survival;
if (score <= 8) {
    survival = '<6';
} else if (score <= 11) {
    survival = '≥6';
} else {
    survival = '≥12';
}

calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: 'Revised Tokuhashi Score'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'Revised Tokuhashi Score'
});
calc_output.push({
    name: 'Mean Survival',
    value: survival,
    value_text: 'months',
    message: 'Mean Survial'
});
