calc.equation_logic_text:
var calc_output = [];

var gender = parseFloat(gender);
var time = parseFloat(time);
var anomaly = parseFloat(anomaly);
var grade = parseFloat(grade);

var index = gender + time + anomaly + grade;

var rate;

if (index == 1) {
    rate = 89;
} else if (index == 2) {
    rate = 69;
} else if (index == 3) {
    rate = 53;
} else if (index == 4) {
    rate = 16;
} else {
    rate = 11;
}

calc_output.push({
    name: 'mini',
    value: index,
    value_text: 'points',
    message: rate + '% rate of resolution'
});
calc_output.push({
    name: 'Index',
    value: index,
    value_text: 'points',
    message: 'VURx'
});
calc_output.push({
    name: 'Resolution',
    value: rate,
    value_text: '%',
    message: 'Rate of resolution'
});
