calc.equation_logic_text:
var calc_output=[]; 

var u=parseInt(age)+parseInt(sex)+parseInt(cancer)+parseInt(heart_failure)+parseInt(chronic_lung_disease)+parseInt(heart_rate_110)+parseInt(systolic_blood_pressure_100)+parseInt(respiratory_rate_30)+parseInt(temperature_36)+parseInt(altered_mental_status)+parseInt(o2_saturation_90);

var msg='';
var mini_msg=''; 

if(u<66){
    msg='Class I, Very Low Risk: 0-1.6% 30-day mortality in this group.';
    mini_msg='Very Low Risk.'
} 
if(u>65&&u<86){
    msg='Class II, Low Risk: 1.7-3.5% 30-day mortality in this group.';
    mini_msg='Low Risk.'
} 
if(u>85&&u<106){
    msg='Class III, Intermediate Risk: 3.2-7.1% 30-day mortality in this group.';
    mini_msg='Intermediate Risk.'
} 
if(u>105&&u<126){
    msg='Class IV, High Risk: 4.0-11.4% 30-day mortality in this group.';
    mini_msg='High Risk.'
} 
if(u>125){
    msg='Class V, Very High Risk: 10.0-24.5% 30-day mortality in this group.';
    mini_msg='Very High Risk.'
} 

calc_output.push({
    name:'mini',
    value:u,
    value_text:'points',
    message:mini_msg
}); 
calc_output.push({
    name:'1304_PESI Score_result',
    value:u,
    value_text:'points',
message:msg
});
